"""Generate fixtures."""

from os import path
import json
from scipy.special import boxcox
import numpy as np

# Get the file path.
FILE = path.realpath(__file__)

# Get the file's directory.
DIR = path.dirname(FILE)


def generate(x, y, filename):
    """Generate fixture data and write to file.

    # Arguments

    * `x`: domain
    * `y`: domain
    * `name::str`: filename of the output file

    # Examples

    ```python
    python> x = np.arange(0.25, 5, 0.25)
    python> y = np.arange(0.25, 5.0, 0.25)
    python> generate(x, y, './data.json')
    ```
    """
    z = boxcox(x, y)
    data = dict(
        x=x.tolist(),
        y=y.tolist(),
        expected=z.tolist()
    )

    filepath = path.join(DIR, filename)

    with open(filepath, 'w') as out:
        json.dump(data, out)


def main():
    """Generate fixture data."""
    x = np.arange(0.25, 5.0, 0.25)
    y = np.arange(0.25, 5.0, 0.25)
    generate(x, y, "./data.json")


if __name__ == "__main__":
    main()
