"""Generate fixtures."""

from os import path
import json
from scipy.special import boxcox
import numpy as np

# Get the file path.
FILE = path.realpath(__file__)

# Get the file's directory.
DIR = path.dirname(FILE)


def generate(domain, filename):
    """Generate fixture data and write to file.

    # Arguments

    * `domain`: domain for function
    * `name::str`: filename of the output file

    # Examples

    ```python
    python> x = np.arange(-10, 10, 0.1)
    python> generate(x, './data.json')
    ```
    """
    data = [dict(input=[x, y], expected=boxcox(x, y)) for x in domain for y in domain]

    filepath = path.join(DIR, filename)

    with open(filepath, 'w') as out:
        json.dump(data, out)


def main():
    """Generate fixture data."""
    x = np.arange(-10, 10, 0.1)
    generate(x, "./data.json")


if __name__ == "__main__":
    main()
